import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import Chart from "react-google-charts";
import io from 'socket.io-client';
import { useEffect, useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid } from 'recharts';

const socket = io('wss://le-18262636.bitzonte.com', {
  path: '/stocks'
});

const Stocks = ({}) => {
  const [stocks, setStocks] = useState([]);
  useEffect(() =>{
    socket.on('UPDATE', data => {
      var j = 0;
      for (var i in stocks){
        if(i.ticker == data.ticker)
        {
          i.value = data.value;
          j = 1;
        }
      }
      if(j==0)
      {
        setStocks(currentData => [...currentData, data]);
      }
    })
  }, [])


  return(
    <div>
      <h1>Real Time CPU Usage</h1>
      <BarChart width={600} height={300} data={stocks}>
    <XAxis dataKey="name" stroke="#8884d8" />
    <YAxis />
    <Tooltip />
    <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
    <Bar dataKey="value" fill="#8884d8" barSize={30} />
  </BarChart>
    </div>
  );
};

ReactDOM.render(<Stocks />, document.getElementById('root'));



