import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import Chart from "react-google-charts";
import io from 'socket.io-client';
import { useEffect, useState } from 'react';
import {
  BarChart,
  Bar,
  Line,
  LineChart,
  XAxis,
  YAxis,
  Tooltip
} from 'recharts';

const socket = io('wss://le-18262636.bitzonte.com', {
  path: '/stocks'
});

const Stocks = ({}) => {
  const [stocks, setStocks] = useState([]);
  useEffect(() =>{
    socket.on('UPDATE', ticker => {
      console.log(ticker);
      setStocks(stocks.push(ticker));
      console.log(stocks)
    })
  }, [])


  return(
    <div>
      <h1>Real Time CPU Usage</h1>
      <LineChart width={500} height={300} data={stocks}>
        <XAxis dataKey="ticker" />
        <YAxis />
        <Line dataKey="value" />
      </LineChart>
    </div>
  );
};

ReactDOM.render(<Stocks />, document.getElementById('root'));



